<?php error_reporting("E_NOTICE");require_once("class.inputfilter.php");if (isset($_POST["xss_simple"])){//Filtro de datos simples  $filtrar = new InputFilter();  $variable_1 = $filtrar->process($_POST["xss_1"]);  $variable_2 = $filtrar->process($_POST["xss_2"]);}if (isset($_POST["xss_excepciones"])){//Filtro de datos con excepciones tags b | i  $filtrar = new InputFilter(array("b", "i"));  $variable_1 = $filtrar->process($_POST["xss_3"]);  $variable_2 = $filtrar->process($_POST["xss_4"]);}?><html><head><title>EVITAR ATAQUES XSS</title></head><body><center><a href="http://php-estudios.blogspot.com">PHP ESTUDIOS</a><br><?php echo $variable_1."<br>".$variable_2; ?><h3>FILTRO DE DATOS SIMPLE<h3><form method="post"><input type="text" name="xss_1" value="<script>alert('XSS 1');</script>"><input type="text" name="xss_2" value="<script>alert('XSS 2');</script>"><input type="hidden" name="xss_simple"><input type="submit" value="No XSS Simple"></form><h3>FILTRO DE DATOS CON EXCEPCIONES. Permitir tags b | i</h3><form method="post"><input type="text" name="xss_3" value="<b>En negrita</b><a href='hack.php'> No Hack</a>"><input type="text" name="xss_4" value="<i>En italica</i><a href='hack.php'> No Hack</a>"><input type="hidden" name="xss_excepciones"><input type="submit" value="No XSS + Excepciones"></form></center></body></html>